CustomField.description:
IF( ARR_Product_Total_Amount__c = 0 && Amount__c!= 0,
  IF(  
     (Amount__c < 150000) , 'Bronze', 
      IF( (Amount__c < 300000),'Silver', 
        IF( (Amount__c < 700000),'Gold',
         IF( (Amount__c < 1500000),'Platinum',
          IF( (Amount__c >= 1500000),'Super Platinum','')
         )
       )
      )
    ), IF(  
      (ARR_Product_Total_Amount__c < 150000) , 'Bronze', 
        IF( (ARR_Product_Total_Amount__c < 300000),'Silver', 
          IF( (ARR_Product_Total_Amount__c < 700000),'Gold',
            IF( (ARR_Product_Total_Amount__c < 1500000),'Platinum', 
              IF( (ARR_Product_Total_Amount__c >= 1500000),'Super Platinum','')
           )
         )
     )
   )
 )

